  // Fiber
  this.return = null; // \u6307\u5411\u5B83\u5728Fiber\u8282\u70B9\u6811\u4E2D\u7684\`parent\`\uFF0C \u7528\u6765\u5728\u5904\u7406\u5B8C\u8FD9\u4E2A\u8282\u70B9\u4E4B\u540E\u5411\u4E0A\u8FD4\u56DE
  this.child = null; // \u6307\u5411\u81EA\u5DF1\u7684\u7B2C\u4E00\u4E2A\u5B50\u8282\u70B9
  this.sibling = null; // \u6307\u5411\u81EA\u5DF1\u7684\u5144\u5F1F\u8282\u70B9\uFF0C\u5144\u5F1F\u8282\u70B9\u7684return\u6307\u5411\u540C\u4E00\u4E2A\u7236\u8282\u70B9
  this.index = 0; // \u7D22\u5F15\uFF0C\u662F\u7236\u8282\u70B9fiber\u4E0B\u7684\u5B50\u8282\u70B9fiber\u4E2D\u7684\u4E0B\u8868

  this.ref = null;  // ref\u7684\u6307\u5411\uFF0C\u53EF\u80FD\u4E3Anull\u3001\u51FD\u6570\u6216\u5BF9\u8C61
  this.refCleanup = null;

  this.pendingProps = pendingProps; // \u65B0\u7684\u3001\u5F85\u5904\u7406\u7684props
  this.memoizedProps = null; // \u4E0A\u4E00\u6B21\u6E32\u67D3\u7684props
  this.updateQueue = null; // \u8BE5Fiber\u5BF9\u5E94\u7684\u7EC4\u4EF6\u4EA7\u751F\u7684Update\u4F1A\u5B58\u653E\u5728\u8FD9\u4E2A\u961F\u5217\u91CC\u9762(\u66F4\u65B0\u961F\u5217)
  this.memoizedState = null; // \u4E0A\u4E00\u6B21\u6E32\u67D3\u7684\u7EC4\u4EF6\u72B6\u6001,\u51FD\u6570\u7EC4\u4EF6\u4FDD\u5B58\u7684hooks\u4FE1\u606F
  this.dependencies = null; // contexts\u3001events\uFF08\u4E8B\u4EF6\u6E90\uFF09 \u7B49\u4F9D\u8D56

  this.mode = mode;  // \u7C7B\u578B\u4E3Anumber\uFF0C\u7528\u4E8E\u63CF\u8FF0fiber\u7684\u6A21\u5F0F 

  // Effects
  this.flags = NoFlags; // effect \u6807\u7B7E,\u7528\u4E8E\u8BB0\u5F55fiber\u7684\u72B6\u6001\uFF08\u5220\u9664\u3001\u65B0\u589E\u3001\u66FF\u6362\u7B49\uFF09
  this.subtreeFlags = NoFlags; // \u5F53\u524D\u5B50\u8282\u70B9\u7684\u526F\u4F5C\u7528\u72B6\u6001
