      // base: 'https://example.com/path/page.html', // \u6CE8\u5165base tag\uFF08false\uFF09
      minify: true, // html \u662F\u5426\u538B\u7F29\uFF08mode = \u2018production\u2019 \u7684\u65F6\u5019\u662Ftrue\uFF0C\u5176\u4ED6false\uFF09
      hash: false, // \u5982\u679Ctrue\uFF0C \u4F1A\u7ED9\u6CE8\u5165\u7684script \u548C css \u6587\u4EF6\u8FFD\u52A0\u989D\u5916\u7684\u60DF\u4E00\u7684compilation hash\uFF0C\u5BF9\u7F13\u5B58\u6709\u6548\uFF0C
