import { useState } from 'react';
import { usePrevious } from './usePrevious';

function App() {
  const [num, setNum] = useState(0);
  const previousNum = usePrevious<number>(num);
  return (
    <>
      <div>previousNum: {previousNum}</div>
      <div>curNum: {num}</div>
      <button onClick={() => setNum(num + 1)}>+</button>
      <button onClick={() => setNum(num - 1)}>-</button>
    </>
  );
}

export default App;

// usePrevious.ts
import { useEffect, useRef } from 'react';

export const usePrevious = <T>(value: T): T | undefined => {
  const ref = useRef<T>();

  useEffect(() => {
    ref.current = value;
  }, [value]); // \u6E32\u67D3\u540E\u518D\u6267\u884C\uFF0C\u66F4\u6539\u540E\u7684\u503C\u4F1A\u5B58\u5230ref.current\uFF0C\u4E0B\u6B21\u518D\u6E32\u67D3

  return ref.current; // \u4E00\u8FDB\u6765\u5148\u6E32\u67D3\u4E86\u4E4B\u524D\u5B58\u7684\u503C 
};
