_transformTstoJs(envOptions, tempClonePath) {
  log('[Sylvanas] Prepare js environment...');
  const tsFiles = globList(['**/*.tsx', '**/*.ts'], {
    ...envOptions,
    ignore: ['**/*.d.ts'],
  });

  // \u5C06ts \u8F6C\u6362\u6210 js
  sylvanas(tsFiles, {
    ...envOptions,
    action: 'overwrite',
  });

  log('[JS] Clean up...');
  const removeTsFiles = globList(['tsconfig.json', '**/*.d.ts'], envOptions);
  this._removeFiles(removeTsFiles, tempClonePath);
}
