module.exports = smp.wrap(
  merge(commonConfig, {
    plugins: [
      new BundleAnalyzerPlugin(), // \u914D\u7F6E\u5206\u6790\u6253\u5305\u7ED3\u679C\u63D2\u4EF6
    ],
  }),
);
