const ShareWorker1: React.FC = () => {
    const workerRef = useRef<SharedWorker>();
    const [logout, setLogout] = useState(false);
    const logoutLabel = logout ? "\u5DF2\u9000\u51FA" : "\u5DF2\u767B\u9646";
    const logoutActionLabel = logout ? "\u767B\u9646" : "\u9000\u51FA";

    useEffect(() => {
      const worker = new SharedWorker(new URL('./worker2.ts', import.meta.url), {
        name: 'shareWorker'
      });
      workerRef.current = worker;
      worker.port.start();
    }, [])

    const handleLogout = useCallback(() => {
        const isLogout = !logout;
        workerRef.current.port.postMessage({ logout: isLogout, pageId: 'page1'});
    }, [logout, workerRef]);

    useEffect(() => {
        if(!workerRef.current) return;
        workerRef.current.port.onmessage = (e) => {
          setLogout(e.data.logout);
        }
    })

    return (
        <div>
          <Button onClick={handleLogout}>{logoutActionLabel}</Button>
          <div>page1,\u4F60\u73B0\u5728\u7684\u767B\u9646\u72B6\u6001\uFF1A{logoutLabel}</div>
        </div>
    )
}

export default ShareWorker1;
