const workerList: Worker[] = [];

self.addEventListener('connect', (e: Event) => {
  const port = e.ports[0];

  port.addEventListener('message', (e: MessageEvent) => {
    logout = e.data.logout;
    const pageId = e.data.pageId;
    workerList.forEach(port => {
      port.postMessage({ logout, pageId });
    })
  })
  port.start();
  workerList.push(port);
  port.postMessage({ logout, pageId: 'page1' });
})
