import { useState } from 'react';
import { useThrottle } from './hooks';

function App() {
  const [num, setNum] = useState(0);
  const throttleNum = useThrottle(num, 1000);
  return (
    <>
      <div>throttleNum: {throttleNum}</div>
      <div>curNum: {num}</div>
      <button onClick={() => setNum(num + 1)}>+</button>
      <button onClick={() => setNum(num - 1)}>-</button>
    </>
  );
}

export default App;

// useThrottle.ts
import { useState, useEffect, useRef} from 'react';

export function useThrottle<T>(value: T, delay: number): T | undefined {
  const [state, setState] = useState(value);
  const ref = useRef(Date.now());

  useEffect(() => {
    const now = Date.now();
    if(now - ref.current >= delay) {
      setState(value);
      ref.current = now;
    }
  }, [value]);

  return state;
}
