_handlePackage(envOptions, tempClonePath) {
  const packageJsonPath = path.resolve(tempClonePath, 'package.json');
  const pkg = require(packageJsonPath);
  // gen package.json
  if (pkg['artemis']) {
    const { ignoreScript = [], ignoreDependencies = [] } = pkg['artemis'];
    // filter scripts and devDependencies
    const projectPkg = {
      ...pkg,
      version: '0.0.1',
      scripts: filterPkg(pkg.scripts, ignoreScript),
      devDependencies: filterPkg(pkg.devDependencies, ignoreDependencies),
    };
    // remove artemis config
    delete projectPkg['artemis'];
    fs.writeFileSync(
      path.resolve(tempClonePath, 'package.json'),
      // \u5220\u9664\u4E00\u4E2A\u5305\u4E4B\u540E json\u4F1A\u591A\u4E86\u4E00\u4E9B\u7A7A\u884C\u3002sortPackage \u53EF\u4EE5\u5220\u9664\u6389\u5E76\u4E14\u6392\u5E8F
      // prettier \u4F1A\u5BB9\u5FCD\u4E00\u4E2A\u7A7A\u884C
      prettier.format(JSON.stringify(sortPackage(projectPkg)), {
        parser: 'json',
      }),
    );
  }

  // Clean up useless files
  if (pkg['artemis'] && pkg['artemis'].ignore) {
    log('Clean up...');
    const ignoreFiles = pkg['artemis'].ignore;
    const fileList = globList(ignoreFiles, envOptions);
    this._removeFiles(fileList, tempClonePath);
  }
}
