Hook(): Hook {
  // 1.\u521B\u5EFAhooks\u5BF9\u8C61
  const hook: Hook = {
    // useState\u4E2D \u4FDD\u5B58 state\u4FE1\u606F \uFF5C useEffect \u4E2D \u4FDD\u5B58\u7740 effect \u5BF9\u8C61 \uFF5C useMemo \u4E2D \u4FDD\u5B58\u7684\u662F\u7F13\u5B58\u7684\u503C\u548Cdeps \uFF5C useRef\u4E2D\u4FDD\u5B58\u7684\u662Fref \u5BF9\u8C61
    memoizedState: null, 
    // useState\u548C useReducer\u4E2D,\u4E00\u6B21\u66F4\u65B0\u4E2D\uFF0C\u4EA7\u751F\u7684\u6700\u65B0 state\u503C\u3002
    baseState: null, 
    // useState\u548CuseReducer\u4E2D \u4FDD\u5B58\u6700\u65B0\u7684\u66F4\u65B0\u961F\u5217\u3002
    baseQueue: null,
    // \u4FDD\u5B58\u5F85\u66F4\u65B0\u961F\u5217 pendingQueue \uFF0C\u66F4\u65B0\u51FD\u6570 dispatch \u7B49\u4FE1\u606F\u3002 
    queue: null,
    // \u6307\u5411\u4E0B\u4E00\u4E2A hooks\u5BF9\u8C61\u3002
    next: null, 
  };
  // 2.\u5224\u65AD\u662F\u5426\u662F\u51FD\u6570\u7EC4\u4EF6\u4E2D\u7B2C\u4E00\u4E2Ahooks
  if (workInProgressHook === null) {
    // hooks\u94FE\u8868\u4E2D\u7684\u7B2C\u4E00\u4E2A
    currentlyRenderingFiber.memoizedState = workInProgressHook = hook;
  } else {
    // \u6DFB\u52A0\u5230 \u94FE\u8868\u7684\u6700\u540E
    workInProgressHook = workInProgressHook.next = hook;
  }
  return workInProgressHook;
}
