import { useDispatch, useSelector } from 'react-redux';
import { setCurrentTabInfo, setTabInfoList } from '@/store/global';
import { getCurrentTabInfo } from '@/layout/components/DynamicTabs';
import { authTree } from '@/layout/authTree';

interface OptionsProps extends NavigateOptions {
  target?: string; // \u65B0\u5F00\u9875\u9762 '_blank'
  closePath?: string; // \u5BFC\u5165\u67D0\u4E2A\u9875\u9762\u7684\u65F6\u5019\uFF0C \u5173\u95ED(\u5F53\u524D)\u5176\u4ED6\u9875\u9762
}

export default function useAppNavigate() {
  const navigateTo = useNavigate();
  const dispatch = useDispatch();
  const { tabInfoList } = useSelector(({ global }) => global);

  return (path: string, options?: OptionsProps) => {
    const currentTabInfoFromPath = getCurrentTabInfo({ path, authTree });
    // \u5BFC\u822A\u5230\u65B0\u9875\u9762\u65F6\uFF0C\u662F\u65B0\u5F00\u4E00\u4E2Atab\u9875
    if (options?.target === '_blank') {
      dispatch(setCurrentTabInfo(currentTabInfoFromPath));
    }
    // \u5BFC\u822A\u5230\u67D0\u4E2A\u9875\u9762\u7684\u65F6\u5019\uFF0C \u5173\u95ED(\u5F53\u524D)\u5176\u4ED6\u9875\u9762
    if (options?.closePath) {
      const newTabInfoList = tabInfoList.filter(
        ({ tabRouter }) => tabRouter !== options?.closePath
      );
      dispatch(setTabInfoList(newTabInfoList));
    }
    navigateTo(path, options);
  };
}
