export default App;

// useThrottle.ts
import { useState, useEffect, useRef} from 'react';

export function useThrottle<T>(value: T, delay: number): T | undefined {
  const [state, setState] = useState(value);
  const ref = useRef(Date.now());

  useEffect(() => {
    const now = Date.now();
    if(now - ref.current >= delay) {
      setState(value);
      ref.current = now;
    }
  }, [value]);

  return state;
}
